// buttonClasses generates shared button styling classes
// variant: "primary" (white bg), "secondary" (outlined), "ghost" (transparent), "danger"
// size: "sm", "md", "lg"
// fullWidth: true to make button full width
func buttonClasses(variant, size string, fullWidth bool) string {
	classes := "font-mono uppercase tracking-wider transition-all border-2 inline-flex items-center justify-center "

	if fullWidth {
		classes += "w-full "
	}

	// Variants
	switch variant {
	case "", "primary":
		classes += "bg-white text-black border-white hover:bg-black hover:text-white active:scale-95 "
	case "secondary":
		classes += "bg-black text-white border-white hover:bg-white hover:text-black active:scale-95 "
	case "ghost":
		classes += "bg-transparent text-white border-white/20 hover:border-white active:scale-95 "
	case "danger":
		classes += "bg-black text-white border-white hover:bg-white hover:text-black active:scale-95 "
	}

	// Sizes
	switch size {
	case "sm":
		classes += "px-3 py-1 text-xs"
	case "", "md":
		classes += "px-4 py-2 text-sm"
	case "lg":
		classes += "px-6 py-3 text-base"
	case "mobile-primary":
		// 56px tall button for mobile thumb-friendly primary actions
		classes += "px-6 py-4 text-base min-h-[56px]"
	case "mobile-action":
		// 72px tall button for critical mobile actions (remote player, producer)
		classes += "px-8 py-5 text-lg min-h-[72px]"
	}

	return classes
}

